<script>
const soundMP3 = new Audio();
var linkAudios = [];
var soAudio = 0;
 $(".cauhoitracnghiem .sm2_button").each(function(){
 	var loadMP3 = $(this).attr("href");
    linkAudios.push(loadMP3);
	soAudio ++;
 });
function playingMP3(soluot, solanplay, idplay, thoigiancho, solannghelai, iddautien, nghedanhsach){
	    soluot --;
		var khoangcachthoigian = thoigiancho*1000;
		$('.sm2_button').removeClass('sm2_playing');
		$("a[href='" + linkAudios[idplay] + "']").addClass('sm2_playing');
		soundMP3.src = linkAudios[idplay];
		soundMP3.play();
		if(soluot == 0){
		idplay ++;
		if(nghedanhsach == 0)
		solannghelai = 0;
		soluot = solanplay;
		} 
		idplayNext = idplay;
		soundMP3.onended = function() {
			$('.sm2_button').removeClass('sm2_playing');
			if(solannghelai > 0 && idplayNext < soAudio){
				setTimeout(function(){ 
					playingMP3(soluot, solanplay, idplayNext, thoigiancho, solannghelai, iddautien, nghedanhsach);	
				}, khoangcachthoigian);
			} else{
				solannghelai --;
				if(solannghelai > 0){
					setTimeout(function(){ 
						playingMP3(solanplay, solanplay, iddautien, thoigiancho, solannghelai, iddautien, nghedanhsach);
					}, khoangcachthoigian);
				}
			}
		}
}
$(document).ready(function(){
	$(".noidung_hoctuvung").on("click", ".sm2_button", function() {
	   var nghelientuc = $('#cauhinh_nghelientuc').val();
	   var solannghe = $('#cauhinh_solan').val();
	   var nghelai = $('#cauhinh_nghelai').val();
	   var thoigiandoi = $('#cauhinh_thoigiandoi').val();
	   if(nghelientuc < 1)
		   nghelientuc = 0;
	   if(solannghe < 1)
		   solannghe = 1;
	   if(nghelai < 1)
		   nghelai = 1;
	   if(thoigiandoi < 1)
		   thoigiandoi = 1;
	   var linkmp3 = $(this).attr("href");
	   var idmp3 = jQuery.inArray(linkmp3, linkAudios);
	   playingMP3(solannghe, solannghe, idmp3, thoigiandoi, nghelai, idmp3, nghelientuc);
	   return false;
	});
	
	$('html, body').animate({
		scrollTop: $("#content-blog-view").offset().top
	}, 1000);
			
	$(".list-box.ds-blog").on("click", ".qselect", function() {
	var classid = $(this).data("id");
	$('.qselect.trloi'+classid).removeClass("qchecked");
	$('.qselect.trloi'+classid).removeClass("traloidung");
	$('.qselect.trloi'+classid).removeClass("traloisai");
	$(this).addClass("qchecked");
	$(this).addClass("trloi"+classid);
	var traloi = $(this).data("traloi");
	if(traloi == 'T')
	$(this).addClass('traloidung');	
	else $(this).addClass('traloisai');	
	});
	
	$(".result_view_tracnghiem").on("click", ".sm2_button", function() {
			$('.sm2_button').removeClass('sm2_playing');
			$(this).addClass("sm2_playing");
			var idcau = $(this).data("div");
			soundMP3.src = $('.entry-tracnghiem-test.cauhoi-'+idcau+' audio').attr('src');
			soundMP3.play();
			const divclick = this;
			soundMP3.onended = function() {
				$(divclick).removeClass('sm2_playing');
			}
			
	});

	$('.change-select-dc').click(function() {
		$('.ajax-loader').fadeIn();
		var tab_id = 2;
		var bai_id = 168;
		queryString = 'tab_id='+tab_id+'&bai_id='+bai_id+'&muc=nghehieu';
			jQuery.ajax({
				url: "https://luyenthieps.vn/load-tab-tracnghiem",
				data:queryString,
				type: "POST",
				success:function(data){
					$('.ajax-loader').fadeOut('slow');
					$('.result_view_tracnghiem .data-tracnghiem').html(data);
					if($('.change-select').val() == 1){
					$('.cauhoi-tiengviet').hide();
					$('.cauhoi-tienghan').show();
					} else if($('.change-select').val() == 2){
					$('.cauhoi-tiengviet').show();
					$('.cauhoi-tienghan').hide();
					}
			   }
		});
	});

	$("#tab-menu").on("click", ".title_hoc", function() {
		$('.ajax-loader').fadeIn();
		soundMP3.pause();
		var tab_id = $(this).data("id");
		var bai_id = $(this).data("bai");
		$('.title_hoc').removeClass("active");
		$(this).addClass("active");
		queryString = 'tab_id='+tab_id+'&bai_id='+bai_id+'&muc=nghehieu';
			jQuery.ajax({
				url: "https://luyenthieps.vn/load-tab-tracnghiem",
				data:queryString,
				type: "POST",
				success:function(data){
					$('.ajax-loader').fadeOut('slow');
					if(tab_id == 1){
					$('.result_view_tuvung').show();
					$('.result_view_tracnghiem').hide();
					} else if(tab_id == 2){
					$('.result_view_tracnghiem .data-tracnghiem').html(data);
					$('.result_view_tuvung').hide();
					$('.result_view_tracnghiem').show();	
					}
					
				}
		});
	});

});
</script>	
